from fastapi import FastAPI, Request

app = FastAPI()

AUM_TOTAL = 1_000_000_000  # $1B
ASIA_TECH_ALLOC = {
    "TSM": 220_000_000,
    "INFY": 80_000_000,
    "SSNLF": 50_000_000
}

@app.post("/analyze") 
async def analyze(request: Request):
    data = await request.json()
    asia_data = data.get("asia_tech_brief", [])

    # 1. Exposure Calculation
    exposure_usd = sum(ASIA_TECH_ALLOC.values())
    exposure_percent = round((exposure_usd / AUM_TOTAL) * 100, 2)

    # 2. Earnings Surprise Detection
    surprises = []
    for item in asia_data:
        ticker = item.get("ticker")
        summary = item.get("earnings_summary", "")

        try:
            if "Avg. Estimate" in summary and "Year Ago EPS" in summary:
                parts = summary.split("Avg. Estimate")[1]
                estimate = float(parts.split()[0])

                parts2 = summary.split("Year Ago EPS")[1]
                actual = float(parts2.split()[0])

                diff = round(((actual - estimate) / estimate) * 100, 2)
                if diff >= 1:
                    surprises.append(f"{ticker} beat estimates by {diff}%")
                elif diff <= -1:
                    surprises.append(f"{ticker} missed estimates by {abs(diff)}%")
            else:
                surprises.append(f"{ticker}: insufficient earnings data")
        except Exception as e:
            surprises.append(f"{ticker}: error parsing data")

    return {
        "asia_exposure_percent": exposure_percent,
        "earnings_surprises": surprises
    }

from fastapi import FastAPI
import yfinance as yf

app = FastAPI()

@app.get("/stock_info")
def get_stock_info(ticker: str):
    stock = yf.Ticker(ticker)
    hist = stock.history(period="2d")
    
    if hist.empty:
        return {"error": "No data found for ticker"}
    
    return {
        "ticker": ticker,
        "latest_close": hist['Close'][-1],
        "previous_close": hist['Close'][-2]
    }

from fastapi import FastAPI
import requests
from bs4 import BeautifulSoup

app = FastAPI()

@app.get("/earnings_summary")
def get_earnings_summary(ticker: str):
    url = f"https://finance.yahoo.com/quote/{ticker}/analysis"
    headers = {
        "User-Agent": "Mozilla/5.0"
    }

    try:
        response = requests.get(url, headers=headers)
        soup = BeautifulSoup(response.text, 'html.parser')

        # Find earnings estimate table
        tables = soup.find_all("table")
        if not tables:
            return {"error": "No earnings data found"}

        # Just return the first table's text as a demo
        return {
            "ticker": ticker,
            "earnings_table_snippet": tables[0].get_text()
        }

    except Exception as e:
        return {"error": str(e)}

from fastapi import FastAPI
import requests

app = FastAPI()

API_AGENT_URL = "http://localhost:8001/stock_info"
SCRAPING_AGENT_URL = "http://localhost:8002/earnings_summary"
RETRIEVER_AGENT_URL = "http://localhost:8005/retrieve"
ANALYTICS_AGENT_URL = "http://localhost:8006/analyze"
LANGUAGE_AGENT_URL = "http://localhost:8004/generate_summary"


ASIA_TECH_TICKERS = ["TSM", "INFY", "SSNLF"]

@app.get("/morning_brief")
def get_morning_brief():
    combined_data = []

    for ticker in ASIA_TECH_TICKERS:
        try:
            # API agent
            api_resp = requests.get(API_AGENT_URL, params={"ticker": ticker})
            api_data = api_resp.json()

            # Scraping agent
            scrap_resp = requests.get(SCRAPING_AGENT_URL, params={"ticker": ticker})
            scrap_data = scrap_resp.json()

            combined_data.append({
                "ticker": ticker,
                "market_data": api_data,
                "earnings_summary": scrap_data.get("earnings_table_snippet", "")
            })

        except Exception as e:
            combined_data.append({
                "ticker": ticker,
                "error": str(e)
            })

    #  Call Retriever Agent
    try:
        retrieval_resp = requests.get(RETRIEVER_AGENT_URL, params={"query": "Asia tech risk and earnings", "k": 3})
        retrieval_data = retrieval_resp.json()
        retrieved_chunks = retrieval_data.get("chunks", [])
    except Exception as e:
        retrieved_chunks = [f"Retriever error: {e}"]

    #  Call Analytics Agent
    try:
        analytics_resp = requests.post(ANALYTICS_AGENT_URL, json={"asia_tech_brief": combined_data})
        analytics_data = analytics_resp.json()
    except Exception as e:
        analytics_data = {"error": str(e)}

    return {
        "asia_tech_brief": combined_data,
        "retrieved_context": retrieved_chunks,
        "analytics": analytics_data
    }
